function [s1,s2,d]=zipgetStationData(PATH,FILE)import java.util.*;import java.util.zip.*;import java.io.*;f = ZipFile(PATH);entrada = f.getEntry(FILE);if isempty(entrada)    s1=[];s2=[];d=[];else    ir = BufferedReader(InputStreamReader(f.getInputStream(entrada)));    s1=ir.readLine();    if nargout>1        s2=ir.readLine();        ir.close();        if nargout>2            id = DataInputStream(BufferedInputStream(f.getInputStream(entrada)));                                    ns=length(s1)+length(s2)+4;            n=(entrada.getSize()-ns)/4;                        d=ones(n,1)+NaN;                        id.skip(ns);            for i=1:n                d(i)=id.readFloat();            end                        id.close();        end    endend